<script>
import { RouterLink, RouterView } from "vue-router";
import Links_Data from "./assets/menuLinks.json";
export default {
  data(){
    return {
      myNavLinks: Links_Data,
      isSidebarVisible: true
    }
  },
  methods: {
    kebabToCamel(str){
      // converts kebab case to camel case.
      let convStr = str.replace(/(_\w)/gi, (x) => x.replace('_', ' ').toUpperCase());
      return convStr[0].toUpperCase() + convStr.slice(1);
    },
    toggleSidebar(){
      let isVis = this.isSidebarVisible;
      if(isVis){
        this.$refs.sidebar.classList.add("sidebar--hide");
      }else{
        this.$refs.sidebar.classList.remove("sidebar--hide");
      }
      this.isSidebarVisible = !this.isSidebarVisible;
      let base = this;
      window.setTimeout(function(){
        base.$refs.sidebarHeading.textContent = isVis ? "ESS" : "Employee self service";
      },2000);
    }
  }
}
</script>

